function burgerHeader(){const e=document.querySelector(".burger"),t=document.querySelector(".header"),s=document.querySelector("body");e.addEventListener("click",(()=>{t.classList.contains("active")?(t.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked")):(t.classList.add("active"),e.classList.add("active-burger"),s.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(t.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked"))}))}function activeButton(){document.querySelectorAll(".menu__item-link").forEach((e=>{e.addEventListener(".click",(function(){e.forEach((e=>e.classList.remove(".menu__item-link-active"))),e.classList.add(".menu__item-link-active")}))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}function updateTimer(){future=Date.parse("January 7, 2023 00:00"),now=new Date,diff=future-now,days=Math.floor(diff/864e5),hours=Math.floor(diff/36e5),mins=Math.floor(diff/6e4),secs=Math.floor(diff/1e3),d=days,h=hours-24*days,m=mins-60*hours,s=secs-60*mins,document.getElementById("timer").innerHTML='<div class="timer__down-box"><h2 class="timer__down-box-title">'+d+'</h2><span class="timer__down-text">днів</span></div><div class="timer__down-box"><h2 class="timer__down-box-title">'+h+'</h2><span class="timer__down-text">годин</span></div><div class="timer__down-box"><h2 class="timer__down-box-title">'+m+'</h2><span class="timer__down-text">хвилин</span></div><div class="timer__down-box"><h2 class="timer__down-box-title">'+s+'</h2><span class="timer__down-text">секунд</span></div>'}function activeBtn(){document.querySelectorAll(".timer__btn-button").forEach((e=>{e.addEventListener(".click",(function(){e.forEach((e=>e.classList.remove(".timer__btn-button-active"))),e.classList.add(".timer__btn-button-active")}))}))}burgerHeader(),activeButton(),window.addEventList